const movieTitle = 'Batman';
cy.get('input.form-control.me-2').type(movieTitle);
cy.get('input[type="submit"], button.btn-outline-success').first().click();
// Click on the first movie card
cy.get('.card', {
  timeout: 10000
}).first().click();
// Verify URL change (the app uses /filme)
cy.url().should('include', '/filme');
// Verify detail page elements
cy.get('h1, h2').should('be.visible');
// Overview usually in a p tag
cy.get('p').should('not.be.empty');

// Search and click first movie
cy.get('input.form-control.me-2').type('Batman');
cy.get('input[type="submit"], button.btn-outline-success').first().click();
cy.get('.card').first().click();
cy.url().should('include', '/filme');
// Click on the "Search Filmes" logo in the navbar
cy.contains('Search Filmes').click();
// Verify we are back at home (root)
cy.url().should('eq', Cypress.config().baseUrl + '/');
cy.get('input.form-control.me-2').should('be.visible');
